mapper.select:
select

from activity
where    1=1

and  id = #{id,jdbcType=INTEGER}


and  `name` = #{name,jdbcType=VARCHAR}


  and description = #{description,jdbcType=VARCHAR}


  and `type` = #{type,jdbcType=VARCHAR}


  and `status` = #{status,jdbcType=INTEGER}

and start_time  <= now()
and end_time >= now()

select

from activity
where    1=1

  and  id = #{id,jdbcType=INTEGER}


  and  `name` = #{name,jdbcType=VARCHAR}


  and description = #{description,jdbcType=VARCHAR}


  and `type` = #{type,jdbcType=VARCHAR}


  and `status` = #{status,jdbcType=VARCHAR}

and  end_time    <= now()
